import React, { useState, useEffect } from "react";
import axios from "axios";

const Reviews = () => {
  const [rating, setRating] = useState(null);
  const [reviews, setReviews] = useState(null);

  const url = "https://maps.googleapis.com/maps/api/place/details/json";
  const place_id = "ChIJu05ObHdV2YARcw7PYoI0dqc";
  const apiKey = "AIzaSyAkEavPqM8mkgWd_hAA6vUYW1xfsmW9yEQ";

  useEffect(() => {
    const fetchData = async () => {
      const returnedData = await axios.get(
        `${url}?place_id=${place_id}&fields=name,rating,user_ratings_total&key=${apiKey}`
      );

      const rating = returnedData.data.results.rating;
      const reviews = returnedData.data.results.user_ratings_total;

      setRating(rating);
      setReviews(reviews);

      return returnedData;
    };

    fetchData();
  }, []);

  return (
    <div id="reviews">
      <h3>Google says...</h3>
      <div id="rating">`Compiled Rating: ${rating}`</div>
      <p id="number">{reviews}</p>
    </div>
  );
};

export default Reviews;
