import React, { useState } from "react";
import "../css/Contact.css";

const Contact = () => {
  const [first, setFirst] = useState("");
  const [last, setLast] = useState("");
  const [phone, setPhone] = useState("");
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState("");
  const [emailError, setEmailError] = useState("");

  const handleFirstChange = (event) => {
    setFirst(event.target.value);
    console.log("working right, setting the first name state");
  };

  const handleLastChange = (event) => {
    setLast(event.target.value);
    console.log("working right, setting the last name state");
  };

  const handlePhoneChange = (event) => {
    setPhone(event.target.value);
    console.log("working right, setting the phone state");
  };

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
    console.log("working right, setting the email state");
  };

  const handleMessageChange = (event) => {
    setMessage(event.target.value);
    console.log("working right, setting the message state");
  };

  const validate = () => {
    if (!email.includes("@")) {
      setEmailError("Invalid email format");
    }
    if (emailError) {
      console.log("the email error showing correctly");
      return false;
    }
    return true;
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const isValid = validate();
    if (isValid) {
      console.log({ first, last, phone, email, message });
    }
  };

  return (
    <div id="body">
      <div id="form">
        <h3>---Talk to Us---</h3>
        <form onSubmit={handleSubmit}>
          <label for="first">First Name</label>
          <input
            type="text"
            placeholder="First Name"
            name="first"
            value={first}
            onChange={handleFirstChange}
          ></input>
          <br />

          <label for="last">Last Name</label>
          <input
            type="text"
            placeholder="Last Name"
            name="last"
            value={last}
            onChange={handleLastChange}
          ></input>
          <br />

          <label for="phone">Phone Number</label>
          <input
            type="tel"
            placeholder="Phone Number"
            name="phone"
            value={phone}
            onChange={handlePhoneChange}
          ></input>
          <br />

          <label for="email">Email</label>
          <input
            type="email"
            placeholder="Email"
            name="email"
            value={email}
            onChange={handleEmailChange}
          ></input>
          <br />
          <div style={{ fontSize: 16, color: "red" }}>{emailError}</div>

          <textarea
            id=""
            rows="10"
            cols="50"
            value={message}
            onChange={handleMessageChange}
          >
            Your message...
          </textarea>
          <br />

          <button id="button" type="submit" value="submit">
            Send It
          </button>
        </form>
      </div>
    </div>
  );
};

export default Contact;
