import React, { useState, useEffect } from "react";
import axios from "axios";
import "../css/Reviews.css";

const Reviews = () => {
  const [rating, setRating] = useState(null);
  const [reviews, setReviews] = useState(null);

  const url = "https://maps.googleapis.com/maps/api/place/details/json";
  const place_id = "ChIJu05ObHdV2YARcw7PYoI0dqc";

  useEffect(() => {
    const fetchData = async () => {
      const returnedData = await axios.get(
        `${url}?place_id=${place_id}&fields=name,rating,user_ratings_total&key=${process.env.REACT_APP_API_KEY}`
      );

      const rating = returnedData.data.result.rating;
      const reviews = returnedData.data.result.user_ratings_total;

      console.log(rating);
      console.log(reviews);

      setRating(rating);
      setReviews(reviews);

      return returnedData;
    };

    fetchData();
  }, []);

  return (
    <div id="reviews">
      <h5>Google says...</h5>
      <div id="rating">Overall Rating: {rating}</div>
      <p id="number"># of Reviews: {reviews}</p>
    </div>
  );
};

export default Reviews;
